import React, { useState } from 'react'
import ReactDOM from 'react-dom'

const Button = props =>
  <button onClick={props.handleClick}>{props.text}</button>

const Feedback = (props) => {
  return (
    <div>
      <h1>give feedback</h1>
      <br />
      <Button handleClick={() => props.setGood(props.good + 1)} text={"Good"}/>
      <Button handleClick={() => props.setNeutral(props.neutral + 1)} text={"Neutral"}/>
      <Button handleClick={() => props.setBad(props.bad + 1)} text={"Bad"}/>
    </div>
  )
}

const RenderStat = (props) =>
  <p>{props.text} {props.num}</p>

const Statistics = (props) => {
  return (
    <div>
      <h1>Statistics</h1>
      <RenderStat text="good" num={props.good} />
      <RenderStat text="neutral" num={props.neutral} />
      <RenderStat text="bad" num={props.bad} />
    </div>
  )
}

const App = () => {
  // save clicks of each button to its own state
  const [good, setGood] = useState(0)
  const [neutral, setNeutral] = useState(0)
  const [bad, setBad] = useState(0)

  return (
    <div>
      <Feedback good={good}
                setGood={setGood}
                neutral={neutral}
                setNeutral={setNeutral}
                bad={bad}
                setBad={setBad}
      />
    </div>
  )
}

ReactDOM.render(<App />,
  document.getElementById('root')
)
